/**
 * Consolidation strategy types and interfaces
 */
/**
 * Type of consolidation strategy
 */
export type ConsolidationStrategyType = 'hierarchical' | 'split-by-audience' | 'merge-and-redirect';
/**
 * Consolidation plan generated by a strategy
 */
export interface ConsolidationPlan {
    /** Strategy type used */
    strategy: ConsolidationStrategyType;
    /** Primary/target file */
    primaryFile: string;
    /** Files to be modified */
    filesToModify: Array<{
        file: string;
        action: 'remove-sections' | 'add-references' | 'archive' | 'merge-into' | 'no-change';
        sections?: string[];
        referenceTarget?: string;
    }>;
    /** Human-readable description */
    description: string;
    /** Estimated line reduction */
    estimatedLineReduction: number;
    /** Warnings or considerations */
    warnings: string[];
}
/**
 * Result of executing a consolidation strategy
 */
export interface ConsolidationResult {
    success: boolean;
    plan: ConsolidationPlan;
    changedFiles: string[];
    linesRemoved: number;
    error?: string;
}
/**
 * Base interface for all consolidation strategies
 */
export interface IConsolidationStrategy {
    /**
     * Analyze files and generate consolidation plan
     */
    analyze(files: string[], overlaps: any[]): Promise<ConsolidationPlan>;
    /**
     * Execute the consolidation plan (with dry-run support)
     */
    execute(plan: ConsolidationPlan, dryRun?: boolean): Promise<ConsolidationResult>;
    /**
     * Get strategy name
     */
    getName(): string;
    /**
     * Get strategy description
     */
    getDescription(): string;
}
